import HighchartsWrapper from '../chart/chart.component'

import './daily-data.styles.css';
ReactFC.fcRoot(FusionCharts, Column2D, FusionTheme);
ReactFC.fcRoot(FusionCharts, TimeSeries);
const jsonify = res => res.json();
const dataFetch = fetch(
  "https://s3.eu-central-1.amazonaws.com/fusion.store/ft/data/line-chart-with-time-axis-data.json"
).then(jsonify);
const schemaFetch = fetch(
  "https://s3.eu-central-1.amazonaws.com/fusion.store/ft/schema/line-chart-with-time-axis-schema.json"
).then(jsonify);
const dataSource = {
    chart: {},
    caption: {
      text: "Sales Analysis"
    },
    subcaption: {
      text: "Grocery"
    },
    yaxis: [
      {
        plot: {
          value: "Grocery Sales Value"
        },
        format: {
          prefix: "$"
        },
        title: "Sale Value"
      }
    ]
  };
const chartData = [
    {
      label: "Venezuela",
      value: "290"
    },
    {
      label: "Saudi",
      value: "260"
    },
    {
      label: "Canada",
      value: "180"
    },
    {
      label: "Iran",
      value: "140"
    },
    {
      label: "Russia",
      value: "115"
    },
    {
      label: "UAE",
      value: "100"
    },
    {
      label: "US",
      value: "30"
    },
    {
      label: "China",
      value: "30"
    }
  ];
const chartConfigs = {
    type: "column2d", // The chart type
    width: "700", // Width of the chart
    height: "400", // Height of the chart
    dataFormat: "json", // Data type
    dataSource: {
      // Chart Configuration
      chart: {
        caption: "Countries With Most Oil Reserves [2017-18]",    //Set the chart caption
        subCaption: "In MMbbl = One Million barrels",             //Set the chart subcaption
        xAxisName: "Country",           //Set the x-axis name
        yAxisName: "Reserves (MMbbl)",  //Set the y-axis name
        numberSuffix: "K",
        theme: "fusion"                 //Set the theme for your chart
      },
      // Chart Data - from step 2
      data: chartData
    }
  };
// const DailyData = props => (
// <h2>fg</h2>

// // );

// export default DailyData;

class DailyData extends Component {

    constructor() {
        super();
        this.onFetchData = this.onFetchData.bind(this);

        this.state = {
            imgUrl:'',
          lat:'',
          lon:'',
          appId:'9b66a9c4055d9d1f99e3bdcdd1dbfa69',
          dailyData:{},
          hourlyData:[],
          timeseriesDs: {
            type: "timeseries",
            renderAt: "container",
            width: "600",
            height: "400",
            dataSource
          },
          series: [{
            data: []
          }],
          options : {
            chart: {
              type: 'spline'
            },
            title: {
              text: 'My chart'
            },
            xAxis: {
                title: {
                  text: ""
                },
                labels: {
                    formatter: function() {
                      return this.value;
                    }
                  },
                categories:[]
              },
            // series: [
            //   {
            //     data: []
            //   }
            // ]

          }
        };


          // Create a JSON object to store the chart configurations

    }

    componentDidMount() {
        this.onFetchData();
        const lat = this.props.location.state.lat
        const lon = this.props.location.state.lon
        console.log(this.props.match.params.day)
        axios
        .get(
          "https://api.openweathermap.org/data/2.5/onecall?lat=" +
            lat+
            "&lon=" +
           lon + "&exclude="+
            "&APPID=" +
            this.state.appId + "&units=metric" 
        ).then(response =>{
          this.setState({dailyData:response.data.current});
          console.log(this.state.dailyData.weather[0].icon)
          this.setState({imgUrl:this.state.dailyData.weather[0].icon});
          //const imgUrl = this.state.dailyData.weather[0].icon;

        //   let newArray = {...this.state.options.chart}
        //   let newArray1 = {...this.state.options.title}
        //   console.log(newArray,newArray1)
        // const options = {...this.state};
        // console.log('nn',options)
        // const options1 = {...options.options};
        // console.log('nn1',options1)
        // const options3 = {...options1.series};
        // console.log('nn1',options3)
        // const options4 = {...options3.data};
        // console.log('nn1',options4)

        //this.setState({});
        //   const newAccess1 = {...this.state.xAxis};
          //const newAccess2 = {...newAccess.series};
          //const newAccess3 = {...newAccess2.data};
          //console.log(newAccess2,newAccess3)
//newAccess1.categories = response.data.hourly;
// const newarr = {...this.state.series}
// const newarr1 = {...newarr.data}
// console.log(newarr,newarr1,'fe')
        //   this.setState({options.xAxis.categories:response.data.hourly});
    const info=response.data.hourly.map((girl) => Number(girl.temp.toFixed()));
    console.log(info)
    //this.setState({newarr:info})
    //  const info2 = [{...newarr},info]
    //  this.setState({newarr1:info})
    // console.log(this.state.series.data)
    //const info2 = [{...newArray},{...newArray1},info]
    // this.setState({
    //     todos: newArray,
    //     });
    // //options3 = info;
    // this.setState({options3: info});
    // //this.setState({options3: options4});
    // this.setState({options1: options3});
    // this.setState({options: options1});
        //const info= response.data.hourly.map((girl) => girl.temp.toFixed());
        // console.log('in',info)
        // const info1 = [{...newAccess},info]
        // console.log('iii',info1)
        // this.setState({data: info});
        //console.log(this.state.option3)
        let _this = this;
        //_this.state.series[0].data = info;
        _this.setState({ series: [{ data: info }] });
        })
    }

    onFetchData() {
        console.log(dataFetch,schemaFetch)
        Promise.all([dataFetch, schemaFetch]).then(res => {
          const data = res[0];
          const schema = res[1];
          const fusionTable = new FusionCharts.DataStore().createDataTable(
            data,
            schema
          );
          const timeseriesDs = Object.assign({}, this.state.timeseriesDs);
          timeseriesDs.dataSource.data = fusionTable;
          this.setState({
            timeseriesDs
          });
        });
      }

    render() {
      return (
          <div className="outer-div">
           {/* <ReactFC {...chartConfigs} /> */}

        <div className='dailycard-container temp_container'>

        <h2>{parseFloat(this.state.dailyData.temp).toFixed()}&#8451;</h2>
        <div className="img-div">
          <img
          alt='monster'
          src={`http://openweathermap.org/img/w/${this.state.imgUrl}.png`}
        />
        <p><Moment unix format='ddd'>{this.state.dailyData.dt}</Moment></p>
        </div>
        <div className="info-div">
          <p>Latitude</p>
          <h5>{this.props.location.state.lat}</h5>
        </div>
        <div className="info-div">
          <p>Longitude</p>
          <h5>{this.props.location.state.lon}</h5>
        </div>
        <div className="info-div">
          <p>Pressure</p>
          <h5>{this.state.dailyData.pressure} hpa</h5>
        </div>
        <div className="info-div">
          <p>Humidity</p>
          <h5>{this.state.dailyData.humidity} %</h5>
        </div>
        <div>
    {/* <HighchartsReact highcharts={Highcharts} 
    options={this.state.options}
    chartData={this.state.series}
     /> */}
     {/* <ReactFC {...this.state.timeseriesDs} /> */}
     <HighchartsWrapper
        chartData={this.state.series}
      />
  </div>

        <div className="info-div">
          <p>Sunrise</p>
          <h5><Moment unix format='h:mm a'>{this.state.dailyData.sunrise}</Moment></h5>
        </div>
        <div className="info-div">
          <p>Sunset</p>
          <h5><Moment unix format='h:mm a'>{this.state.dailyData.sunset}</Moment></h5>
        </div>

      </div>
      </div>
      );
    }
  }

  export default DailyData;
