        <h2>{parseFloat(this.state.dailyData.temp).toFixed()}&#8451;</h2>
        <div className="img-div">
          {/* {<img
          alt='monster'
          src={`http://openweathermap.org/img/w/${this.state.dailyData.weather[0].icon}.png`}
        />} */}
        <p><Moment unix format='ddd'>{this.state.dailyData.dt}</Moment></p>
        </div>
        <div className="info-div">
          <p>Latitude</p>
          <h5>{this.props.location.state.lat}</h5>
        </div>
        <div className="info-div">
          <p>Longitude</p>
          <h5>{this.props.location.state.lon}</h5>
        </div>
        <div className="info-div">
          <p>Pressure</p>
          <h5>{this.state.dailyData.pressure} hpa</h5>
        </div>
        <div className="info-div">
          <p>Humidity</p>
          <h5>{this.state.dailyData.humidity} %</h5>
        </div>
        <div>
    <HighchartsReact highcharts={Highcharts} 
    options={this.state.options}
     />
  </div>

        <div className="info-div">
          <p>Sunrise</p>
          <h5><Moment unix format='h:mm a'>{this.state.dailyData.sunrise}</Moment></h5>
        </div>
        <div className="info-div">
          <p>Sunset</p>
          <h5><Moment unix format='h:mm a'>{this.state.dailyData.sunset}</Moment></h5>
        </div>

      </div>
      </div>
      );
    }
  }

  export default DailyData;
