import React, { Component } from 'react';
import axios from 'axios';

import { CardList } from './components/card-list/card-list.component';
import { SearchBox } from './components/search-box/search-box.component';

import './App.css';
const google = window.google;

class App extends Component {
  constructor() {
    super();

    this.state = {
      monsters:[],
      lat:'',
      lon:'',
      searchField: '',
      appId:'9b66a9c4055d9d1f99e3bdcdd1dbfa69',
      weeklyData:[]
    };
    this.autocompleteInput = React.createRef();
    this.autocomplete = null;
    //this.handlePlaceChanged = this.handlePlaceChanged.bind(this);
    //this.getWeather = this.getWeather.bind(this);
    const google = window.google;
  }

  componentDidMount() {
    this.autocomplete = new google.maps.places.Autocomplete(this.autocompleteInput.current,
      {"types": ["geocode"]});

  this.autocomplete.addListener('place_changed', this.handlePlaceChanged);
    fetch('https://jsonplaceholder.typicode.com/users')
     .then(response => response.json())
    .then(users => this.setState({ monsters: users }));


      if ("geolocation" in navigator) { 
        //document.getElementById("loader").style.display = "block";
        //document.getElementById("myDiv").style.opacity = "0.1";
       navigator.geolocation.getCurrentPosition(
        (position) => {
          this.setState({lat:position.coords.latitude,lon:position.coords.longitude},() => {this.getWeather();});
        },
        (error) => {
          console.log('location1')
          axios.get('http://ip-api.com/json')
          .then(response => {
            this.setState({lat:response.data.lat,lon:response.data.lon},() => {this.getWeather();});
          })

        }
      );
      } else {
        // fetch("http://ip-api.com/json")
        // .then(response => response.json())
        // .then(json => this.setState({location:json}));
        console.log('location')
        axios.get('http://ip-api.com/json')
        .then(response => {
          this.setState({ location: response.data});
        })
      }


  }

  getWeather = () => {
    const lat = this.state.lat
    const lon = this.state.lon
    const appId = this.state.appId
    // axios
    // .get(
    //   "https://api.openweathermap.org/data/2.5/weather?lat=" +
    //     lat+
    //     "&lon=" +
    //    lon +
    //     "&APPID=" +
    //     appId+ "&units=metric"
    // )
    axios
    .get(
      "https://api.openweathermap.org/data/2.5/onecall?lat=" +
        lat+
        "&lon=" +
       lon + "&exclude=hourly"+
        "&APPID=" +
        appId + "&units=metric"
    ).then(response =>{
      this.setState({weeklyData:response.data.daily});
    })

  }

  handlePlaceChanged = () => {
    const place = this.autocomplete.getPlace();
   const lat = place.geometry.location.lat()
   var lng = place.geometry.location.lng();
    //this.props.onPlaceLoaded(place);
  }

  onSearchChange = event => {
    this.setState({ searchField: event.target.value });
  };

  render() {
    const { monsters, searchField } = this.state;
    const filteredMonsters = monsters.filter(monster =>
      monster.name.toLowerCase().includes(searchField.toLowerCase())
    );

    return (
      <div className='App'>
        <h1>Monsters Rolodex</h1>
        <SearchBox onSearchChange={this.onSearchChange} />
        <CardList weeklyData={this.state.weeklyData} lat={this.state.lat} lon={this.state.lon}/>
        <input ref={this.autocompleteInput}  id="autocomplete" placeholder="Enter your address"
         type="text"></input>
      </div>
    );
  }
}

export default App;
