import React, { Component } from 'react';
import axios from 'axios';
import './daily-data.styles.css';


// const DailyData = props => (
// <h2>fg</h2>

// // );

// export default DailyData;

class DailyData extends Component {

    constructor() {
        super();

        this.state = {
          lat:'',
          lon:'',
          appId:'9b66a9c4055d9d1f99e3bdcdd1dbfa69',
          dailyData:[],
          options : {
            chart: {
              type: 'spline'
            },
            title: {
              text: 'My chart'
            },
            series: [
              {
                data: [1, 2, 1, 4, 3, 6]
              }
            ]
          }
        };
    }

    componentDidMount() {
        const lat = this.props.location.state.lat
        const lon = this.props.location.state.lon

        axios
        .get(
          "https://api.openweathermap.org/data/2.5/onecall?lat=" +
            lat+
            "&lon=" +
           lon + "&exclude="+
            "&APPID=" +
            this.state.appId + "&units=metric"
        ).then(response =>{
          this.setState({dailyData:response.data});
        })
    }

    render() {
      return (
        <div className='card-container temp_container'>

        <h2>{props.daily.temp.day.toFixed()}&#8451;</h2>
        <div className="img-div">
          {<img
          alt='monster'
          src={`http://openweathermap.org/img/w/${props.daily.weather[0].icon}.png`}
        />}
        <p><Moment unix format='ddd'>{props.daily.dt}</Moment></p>
        </div>
        <div className="info-div">
          <p>Latitude</p>
          <h5>{props.lat}</h5>
        </div>
        <div className="info-div">
          <p>Longitude</p>
          <h5>{props.lon}</h5>
        </div>
        <div className="info-div">
          <p>Pressure</p>
          <h5>{props.daily.pressure} hpa</h5>
        </div>
        <div className="info-div">
          <p>Humidity</p>
          <h5>{props.daily.humidity} %</h5>
        </div>
        <div className="info-div">
          <p>Sunrise</p>
          <h5><Moment unix format='h:mm a'>{props.daily.sunrise}</Moment></h5>
        </div>
        <div className="info-div">
          <p>Sunset</p>
          <h5><Moment unix format='h:mm a'>{props.daily.sunset}</Moment></h5>
        </div>

      </div>
      );
    }
  }

  export default DailyData;
