import React, { Component } from 'react';
import axios from 'axios';
import Moment from 'react-moment';
//import Highcharts from 'highcharts';
//import HighchartsReact from 'highcharts-react-official';

// Step 2 - Include the react-fusioncharts component
import ReactFC from "react-fusioncharts";

// Step 3 - Include the fusioncharts library
import FusionCharts from "fusioncharts";

// Step 4 - Include the chart type
import Column2D from "fusioncharts/fusioncharts.charts";

// Step 5 - Include the theme as fusion
import FusionTheme from "fusioncharts/themes/fusioncharts.theme.fusion";

// Step 6 - Adding the chart and theme as dependency to the core fusioncharts
ReactFC.fcRoot(FusionCharts, Column2D, FusionTheme);

import './daily-data.styles.css';

const chartData = [
    {
      label: "Venezuela",
      value: "290"
    },
    {
      label: "Saudi",
      value: "260"
    },
    {
      label: "Canada",
      value: "180"
    },
    {
      label: "Iran",
      value: "140"
    },
    {
      label: "Russia",
      value: "115"
    },
    {
      label: "UAE",
      value: "100"
    },
    {
      label: "US",
      value: "30"
    },
    {
      label: "China",
      value: "30"
    }
  ];
const chartConfigs = {
    type: "column2d", // The chart type
    width: "700", // Width of the chart
    height: "400", // Height of the chart
    dataFormat: "json", // Data type
    dataSource: {
      // Chart Configuration
      chart: {
        caption: "Countries With Most Oil Reserves [2017-18]",    //Set the chart caption
        subCaption: "In MMbbl = One Million barrels",             //Set the chart subcaption
        xAxisName: "Country",           //Set the x-axis name
        yAxisName: "Reserves (MMbbl)",  //Set the y-axis name
        numberSuffix: "K",
        theme: "fusion"                 //Set the theme for your chart
      },
      // Chart Data - from step 2
      data: chartData
    }
  };
// const DailyData = props => (
// <h2>fg</h2>

// // );

// export default DailyData;

class DailyData extends Component {

    constructor() {
        super();

        this.state = {
          lat:'',
          lon:'',
          appId:'9b66a9c4055d9d1f99e3bdcdd1dbfa69',
          dailyData:{},
          hourlyData:[],
          options : {
            chart: {
              type: 'spline'
            },
            title: {
              text: 'My chart'
            },
            xAxis: {
                title: {
                  text: ""
                },
                categories:[]
              },
            series: [
              {
                data: []
              }
            ]

          }
        };


          // Create a JSON object to store the chart configurations

    }

    componentDidMount() {

        const lat = this.props.location.state.lat
        const lon = this.props.location.state.lon

        axios
        .get(
          "https://api.openweathermap.org/data/2.5/onecall?lat=" +
            lat+
            "&lon=" +
           lon + "&exclude="+
            "&APPID=" +
            this.state.appId + "&units=metric"
        ).then(response =>{
          this.setState({dailyData:response.data.current});


          let newArray = {...this.state.options.chart}
          let newArray1 = {...this.state.options.title}
          console.log(newArray,newArray1)
        // const options = {...this.state};
        // console.log('nn',options)
        // const options1 = {...options.options};
        // console.log('nn1',options1)
        // const options3 = {...options1.series};
        // console.log('nn1',options3)
        // const options4 = {...options3.data};
        // console.log('nn1',options4)

        //this.setState({});
        //   const newAccess1 = {...this.state.xAxis};
          //const newAccess2 = {...newAccess.series};
          //const newAccess3 = {...newAccess2.data};
          //console.log(newAccess2,newAccess3)
//newAccess1.categories = response.data.hourly;

        //   this.setState({options.xAxis.categories:response.data.hourly});
    const info=response.data.hourly.map((girl) => girl.temp.toFixed());
    const info2 = [{...newArray},{...newArray1},info]
    // this.setState({
    //     todos: newArray,
    //     });
    // //options3 = info;
    // this.setState({options3: info});
    // //this.setState({options3: options4});
    // this.setState({options1: options3});
    // this.setState({options: options1});
        //const info= response.data.hourly.map((girl) => girl.temp.toFixed());
        // console.log('in',info)
        // const info1 = [{...newAccess},info]
        // console.log('iii',info1)
        // this.setState({data: info});
        //console.log(this.state.option3)
        })
    }

    render() {
      return (
          <div className="outer-div">
        <div className='dailycard-container temp_container'>
        <ReactFC {...chartConfigs} />
        <h2>{parseFloat(this.state.dailyData.temp).toFixed()}&#8451;</h2>
        <div className="img-div">
          {/* {<img
          alt='monster'
          src={`http://openweathermap.org/img/w/${this.state.dailyData.weather[0].icon}.png`}
        />} */}
        <p><Moment unix format='ddd'>{this.state.dailyData.dt}</Moment></p>
        </div>
        <div className="info-div">
          <p>Latitude</p>
          <h5>{this.props.location.state.lat}</h5>
        </div>
        <div className="info-div">
          <p>Longitude</p>
          <h5>{this.props.location.state.lon}</h5>
        </div>
        <div className="info-div">
          <p>Pressure</p>
          <h5>{this.state.dailyData.pressure} hpa</h5>
        </div>
        <div className="info-div">
          <p>Humidity</p>
          <h5>{this.state.dailyData.humidity} %</h5>
        </div>
        <div>
    <HighchartsReact highcharts={Highcharts} 
    options={this.state.options}
     />
  </div>

        <div className="info-div">
          <p>Sunrise</p>
          <h5><Moment unix format='h:mm a'>{this.state.dailyData.sunrise}</Moment></h5>
        </div>
        <div className="info-div">
          <p>Sunset</p>
          <h5><Moment unix format='h:mm a'>{this.state.dailyData.sunset}</Moment></h5>
        </div>

      </div>
      </div>
      );
    }
  }

  export default DailyData;
